import React from 'react';
import './TimerItem.css';

class Timer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {...props.info, isToggleOn: false};
        this.tick = this.tick.bind(this);
        this.handleStart = this.handleStart.bind(this);
        this.handleStop = this.handleStop.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
    }
    handleStart() {
        if(this.timerID!== undefined) {
            console.log("Timer already started")
            return;
        }
        this.timerID = setInterval(this.tick, 1000);
    }

    handleStop() {
        clearInterval(this.timerID);
        this.setState({initialTime: 0})
    }

    handleEdit() {
        this.setState(prevState => ({
            isToggleOn: !prevState.isToggleOn
        }))
    }

    tick() {
        this.setState(prevState => ({
            initialTime: prevState.initialTime + 1
        }));
    }

    handleCancel = () => console.log("operation cancelled");

    render() {
        return (
            <div className="timer-item">
                <p>Title: {this.state.title}</p>
                <p>Elapsed Time: {this.state.initialTime}</p>
                <button onClick={this.handleStart}>Start</button>
                <button onClick={this.handleStop}>Stop</button>
                <button onClick={this.handleEdit}>Edit</button>
                {this.state.isToggleOn && 
                <form>
                    <label>
                        Update title:
                        <input type="text" value={this.state.title} onChange={this.handleChange}/>
                    </label>
                    <button onClick={this.handleChange}>Submit</button>
                    <button onClick={this.handleCancel}>Cancel</button>
                </form>}
            </div>
        )
    }
}

export default Timer;
